package Package1;

public class WhileDowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int value=0;
		
		while(value<10)
		{
			System.out.println("inside the for loop");
			value++;
		}
		
		do
		{
			System.out.println("inside the do while loop");
		}while(value>10);

	}

}
